      tempBox[index].items = data;
      setBoxList(tempBox);
      toggleItemList(null);
    };
    const removeBox = (index) => {
      setBoxList(boxList.filter((item, _) => _ !== index));
    };
    const addBox = () => {
      setBoxList([
        ...boxList,
        {
          items: [],
        },
      ]);
    };
    const handleAddedCustomer=(e)=>{
tableList.splice(tableList.indexOf(e),1)
settableList([...tableList])
    }
 
    const diListColumn = [ 
      {
        id: "box_no",
        numeric: false,
        disablePadding: false,
        label: "Box No",
      },
      {
        id: "description",
        numeric: false,
        disablePadding: false,
        label: "Description",
      },
      {
        id: "qty",
        numeric: false,
        disablePadding: false,
        label: "qty",
      },
     
    ];
  
    const getDCNo = async () => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/generateDc",
          {},
          "get",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setFormData({ ...formData, dc_no: data.data.dc_no });
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  //Party name
  const getAccountList = async () => {
    // setLoading(true);
    try {
      await CommonController.commonApiCallFilter(
        "logistic/mdc/accountlist",
       {search:""},
        "post",
        "node"
      ).then((data) => {
        console.log(data)
        if (data.status === 200) {
          setaccountList(data.result)
        }
      });
    } catch (err) {
      showErrorToast(err);
    }

  };
  const getAccountContactList = async () => {
    try {
      const search= accountList.filter((val)=>( formData.company_name===val.company_name));
      const data= search.map(val=>{return val.company_id})
      await CommonController.commonApiCallFilter(
        "logistic/mdc/accountcontactlist",
       {search:data[0]},
        "post",
        "node"
      ).then((data) => {
        console.log(data.result)
        if (data.status === 200) {
        setFormData({...formData ,cperson_name:data.result.cperson_name})
        }
        
      });
    } catch (err) {
      showErrorToast(err);
    }

  };
  useEffect(() => {
   getAccountContactList()
  },[partychange]);
    useEffect(() => {
      getDCNo();
      getAccountList()
      // getPreviewDC("30490");
    }, []);
  
    const handleSubmit = (data) => {
      getItemDIList(data.tran_id);
      setSelectedDI(data);
      setFormData({
        ...formData,
        so_no: data.so_no,
        cperson_name: data.cperson_name,
        di_date: moment().format("MM/DD/YYYY"),
        company_name: data.company_name,
        di_no: data.di_no,
        company_id: data.company_id,
        cperson_id: data.cperson_id,
        di_id: data.tran_id,
      });
      toggleDIList();
    };
  
    const getItemDIList = async (id) => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/pickDIItem",
          { id: id },
          "post", 
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setPickItemList(data.data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    const submitMain = async () => {
      setLoading(true);
      try {
        let temp = { ...formData };
        temp.dcDetail = pickItemList;
        await CommonController.commonApiCallFilter(
          "logistics/dc/insertmain",
          temp,
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            getPreviewDC(data.data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
      setLoading(false);
    };
    const getPreviewDC = async (id) => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/previewDC",
          { id: id },
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setFormData(data.data.dc_main[0]);
            setPickItemList(data.data.dc_detail);
            setDITable(true);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    const updateItemById = async (obj) => {
      try {
        const request = {
          tran_id: obj.tran_id,
          box_no: obj.box_no,
          mlfb_no: obj.mlfb_no,
          material_code: obj.material_code,
          serial_no: obj.serial_no,
          user_id: getUserId(),
        };
        await CommonController.commonApiCallFilter(
          "logistics/dc/updateItemById",
          request,
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            console.log(data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
   
    const handleItemChange = (index, event) => {
      let tempList = [...pickItemList];
      tempList[index][event.target.name] = event.target.value;
      setPickItemList(tempList);
    };
  
    const getNextElem = (row, input) => {
      const nextElem = document.getElementById(
        `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
          parseInt(input) > 2 ? "1" : parseInt(input) + 1
        }`
      );
  
      return nextElem
        ? nextElem.disabled
          ? document.getElementById(
              `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
                parseInt(input) > 2 ? "1" : parseInt(input) + 2
              }`
            )
          : document.getElementById(
              `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
                parseInt(input) > 2 ? "1" : parseInt(input) + 1
              }`
            )
        : null;
    };
  
    const switchToInput = (event) => {
      if (event.key === "Enter") {
        const currentElemRow = event.target.id.split("-")[2];
        const currentElemInp = event.target.id.split("-")[3];
        if (currentElemRow) {
          const elem = getNextElem(currentElemRow, currentElemInp);
          if (elem) {
            elem.focus();
          }
        }
      }
    };
  
    return (
      <div className="container-fluid">
        <div className="row mt-5">
        <div className="col-md-3 mb-3">
            <TextField
              label="Date"
              disabled
              fullWidth
              variant="outlined"
              value={moment().format("MM/DD/YYYY")}
              size="small"
              name="date"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="DC NO."
              disabled
              fullWidth
              variant="outlined"
              size="small"
              value={dc_no}
              name={"dc_no"}
            />
          </div>
        
          <div className="col-md-3 mb-3">
            <TextField
              label="PO No."
              
              fullWidth
              variant="outlined"
              value={po_no}
              name="po_no"
              size="small"
            />
          </div>
           <div className="col-md-3 mb-3">
            <DatePicker
              label="PO Date"
              // value={po_date}
              format="dd/MM/yyyy"
              onChange={(date) => handleDateChange("po_date", date)}
              animateYearScrolling
              inputVariant="outlined"
              size="small"
              autoOk
              fullWidth
            />
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="Party Name"
              fullWidth
              variant="outlined"
              size="small"
              value={company_name}
              name="company_name"
              // disabled
              onChange={handleChange}
              select
              >
                {accountList.map((val)=>( <MenuItem  key={val.company_id}value={val.company_name}>{val.company_name}</MenuItem>))}
              
           
              </TextField>
               
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Courier"
              fullWidth
              onChange={handleChange}
              value={courier}
              variant="outlined"
              name="courier"
              size="small"
            />
          </div>
        
          <div className="col-md-3 mb-3">
            <TextField
              label="Vehicle No"
              fullWidth
              value={vehicle_no}
              onChange={handleChange}
              name={"vehicle_no"}
              variant="outlined"
              size="small"
            />
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="Contact Person"
              fullWidth
              variant="outlined"
              value={cperson_name}
              name={"cperson_name"}
              size="small"
              onChange={handleChange}
              // select
            >
              {/* <MenuItem value="ab">ab</MenuItem> */}
            
            </TextField>
          </div>
        
          <div className="col-md-6 mb-3">
            <TextField
              label="Remarks"
              value={remarks}
              fullWidth
              variant="outlined"
              size="small"
              name="remarks"
              onChange={handleChange}/>
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="No Of Box"
              value={no_of_box}
              name="no_of_box"
              fullWidth
              variant="outlined"
              size="small"
              onChange={handleChange}
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Sales Order"
              fullWidth
              variant="outlined"
              value={sales_order}
              onChange={handleChange}
              name="sales_order"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Value"
              fullWidth
              value={value}
              name="value"
              variant="outlined"
              onChange={handleChange}
              size="small"
              multiline
            />
          </div>
        </div> 

      {tableList.length>0?(<SimpleTable
          columns={diListColumn}
          rows={tableList}
          onDelete={handleAddedCustomer}
        />):null
      }  
        <hr/>
        <div className="container-fluid">
        <h1>Add Box:</h1>
          <div className="row mt-5">
           
          <div className="col-md-3 mb-3">
            <TextField
              label="Box No"
              fullWidth
              onChange={handleChange}
              value={box_no}
              variant="outlined"
              name="box_no"
              size="small"
            />
          </div>
          <div className="col-md-4 mb-3">
            <TextField
              label="Description"
              fullWidth
              onChange={handleChange}
              value={description}
              variant="outlined"
              name="description"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="qty"
              fullWidth
              onChange={handleChange}
              value={qty}
              variant="outlined"
              name="qty"
              size="small"
            />
          </div>
          <div className="col-md-2 mb-3 pl-5">
          <Button
          color="primary"
          // className="mr-2"
          // disableElevation
          variant="contained"
          onClick={submitAddBox}
        >
          Add Box
        </Button>
          </div>
          </div>
        </div>

        {/* <div className="float-action-btn">
          <Fab
            variant="extended"
            onClick={addBox}
            size="medium"
            color="primary"
            aria-label="add"
          >
            <AddIcon />
            Add Box
          </Fab>
        </div> */}
      
      <hr />
      <div className="w-100 mt-3 text-right">
        <Button
          color="primary"
          className="mr-2"
          disableElevation
          variant="contained"
          // onClick={submitDC}
        >
          Save
        </Button>
        <Button color="primary" disableElevation variant="contained">
          Cancel
        </Button>
      </div>
    </div>
    
    );
