          />
        </div>
        <div className="col-md-3 mb-5">
          <TextField
            variant="outlined"
            fullWidth
            size="small"
            name="reorder_level"
            value={formData.reorder_level}
            onChange={handleChange}
            label="Re-Order Level Qty."
          />
        </div>
        <div className="col-md-3 mb-5">
          <TextField
            variant="outlined"
            fullWidth
            size="small"
            name="lp_ref"
            value={formData.lp_ref}
            onChange={handleChange}
