import {
    Button,
    formatMs,
    MenuItem,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    TextField,
    Fab
  } from "@material-ui/core";
  import AddIcon from "@material-ui/icons/Add";
  import { DatePicker } from "@material-ui/pickers";
  import moment from "moment";
  import React, { useEffect, useState } from "react";
import { SimpleTable } from "../../../../components/basic-table";
  import { getUserId, showErrorToast } from "../../../../components/common";
  import { TablePicker } from "../../../../components/table-picker";
  import { CommonController } from "../../../../_redux/controller/common.controller";
  
  const AddDeliveryChallanMdc = ({ challanType }) => {
    const [pickItemList, setPickItemList] = useState([]);
    const [showDITable, setDITable] = useState(true);
    const [loading, setLoading] = useState(false);
    const [boxList, setBoxList] = useState([
      {
        items: [],
      },
    ]);
    const [formData, setFormData] = useState({
      di_no: "",
      dc_no: "",
      so_no: "",
      company_id: "",
      company_name: "",
      cperson_id: "",
      di_id: "",
      di_date: "",
      so_date: null,
      cperson_name: "",
      ref_no: "",
      ref_date: null,
      dis_doc_no: "",
      dis_doc_date: null,
      dis_through: "",
      destination: "",
      vehicle_no: "",
      type: "",
      status: "open",
      remarks: "",
      noofbox: "",
      scan_using: "",
      dcDetail: [],
    });
  
    const [showDIList, setShowDIList] = useState(false);
    const [selectedDI, setSelectedDI] = useState(null);
  
    const toggleDIList = () => setShowDIList(!showDIList);
  
    const handleChange = (event) => {
      setFormData({ ...formData, [event.target.name]: event.target.value });
    };
  
    const handleDateChange = (name, date) => {
      setFormData({ ...formData, [name]: date });
    };
  
    const diListColumn = [
      {
        id: "tran_id",
        numeric: false,
        disablePadding: false,
        label: "",
      },
      {
        id: "di_no",
        numeric: false,
        disablePadding: false,
        label: "DI No",
      },
      {
        id: "date",
        numeric: false,
        disablePadding: false,
        label: "DI Date",
      },
      {
        id: "company_name",
        numeric: false,
        disablePadding: false,
        label: "Party",
      },
      {
        id: "cperson_name",
        numeric: false,
        disablePadding: false,
        label: "Contact",
      },
      {
        id: "indent_no",
        numeric: false,
        disablePadding: false,
        label: "Indent No",
      },
      {
        id: "so_no",
        numeric: false,
        disablePadding: false,
        label: "SO No",
      },
      {
        id: "so_date",
        numeric: false,
        disablePadding: false,
        label: "SO Date",
      },
      {
        id: "remarks",
        numeric: false,
        disablePadding: false,
        label: "Remarks",
      },
    ];
  
    const getDCNo = async () => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/generateDc",
          {},
          "get",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setFormData({ ...formData, dc_no: data.data.dc_no });
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    useEffect(() => {
      getDCNo();
      // getPreviewDC("30490");
    }, []);
  
    const handleSubmit = (data) => {
      getItemDIList(data.tran_id);
      setSelectedDI(data);
      setFormData({
        ...formData,
        so_no: data.so_no,
        cperson_name: data.cperson_name,
        di_date: moment().format("MM/DD/YYYY"),
        company_name: data.company_name,
        di_no: data.di_no,
        company_id: data.company_id,
        cperson_id: data.cperson_id,
        di_id: data.tran_id,
      });
      toggleDIList();
    };
  
    const getItemDIList = async (id) => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/pickDIItem",
          { id: id },
          "post", 
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setPickItemList(data.data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    const submitMain = async () => {
      setLoading(true);
      try {
        let temp = { ...formData };
        temp.dcDetail = pickItemList;
        await CommonController.commonApiCallFilter(
          "logistics/dc/insertmain",
          temp,
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            getPreviewDC(data.data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
      setLoading(false);
    };
    const getPreviewDC = async (id) => {
      try {
        await CommonController.commonApiCallFilter(
          "logistics/dc/previewDC",
          { id: id },
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            setFormData(data.data.dc_main[0]);
            setPickItemList(data.data.dc_detail);
            setDITable(true);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    const updateItemById = async (obj) => {
      try {
        const request = {
          tran_id: obj.tran_id,
          box_no: obj.box_no,
          mlfb_no: obj.mlfb_no,
          material_code: obj.material_code,
          serial_no: obj.serial_no,
          user_id: getUserId(),
        };
        await CommonController.commonApiCallFilter(
          "logistics/dc/updateItemById",
          request,
          "post",
          "node"
        ).then((data) => {
          if (data.status === 200) {
            console.log(data);
          } else {
            showErrorToast(data.message);
          }
        });
      } catch (err) {
        showErrorToast(err);
      }
    };
  
    const {
      dc_no,
      so_no,
      cperson_name,
      so_date,
      ref_date,
      remarks,
      scan_using,
      noofbox,
      destination,
      dis_doc_date,
      dis_doc_no,
      dis_through,
      vehicle_no,
      ref_no,
      type,
      status,
    } = formData;
  
    const handleItemChange = (index, event) => {
      let tempList = [...pickItemList];
      tempList[index][event.target.name] = event.target.value;
      setPickItemList(tempList);
    };
  
    const getNextElem = (row, input) => {
      const nextElem = document.getElementById(
        `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
          parseInt(input) > 2 ? "1" : parseInt(input) + 1
        }`
      );
  
      return nextElem
        ? nextElem.disabled
          ? document.getElementById(
              `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
                parseInt(input) > 2 ? "1" : parseInt(input) + 2
              }`
            )
          : document.getElementById(
              `row-input-${parseInt(input) == 3 ? parseInt(row) + 1 : row}-${
                parseInt(input) > 2 ? "1" : parseInt(input) + 1
              }`
            )
        : null;
    };
  
    const switchToInput = (event) => {
      if (event.key === "Enter") {
        const currentElemRow = event.target.id.split("-")[2];
        const currentElemInp = event.target.id.split("-")[3];
        if (currentElemRow) {
          const elem = getNextElem(currentElemRow, currentElemInp);
          if (elem) {
            elem.focus();
          }
        }
      }
    };
  
    return (
      <div className="container-fluid">
        <div className="row mt-5">
        <div className="col-md-3 mb-3">
            <TextField
              label="Date"
              disabled
              fullWidth
              variant="outlined"
              value={moment().format("MM/DD/YYYY")}
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="DC NO."
              disabled
              fullWidth
              variant="outlined"
              size="small"
              value={""}
            />
          </div>
        
          <div className="col-md-3 mb-3">
            <TextField
              label="PO No."
              disabled
              fullWidth
              variant="outlined"
              value={""}
              size="small"
            />
          </div>
           <div className="col-md-3 mb-3">
            <DatePicker
              label="PO Date"
              value={so_date}
              format="dd/MM/yyyy"
              onChange={(date) => handleDateChange("so_date", date)}
              animateYearScrolling
              inputVariant="outlined"
              size="small"
              autoOk
              fullWidth
            />
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="Party Name"
              fullWidth
              variant="outlined"
              size="small"
              // disabled
              select
              >
               <MenuItem value="Project">Project</MenuItem>
              <MenuItem value="Spare">Spare</MenuItem> 
              </TextField>
               
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Courier"
              fullWidth
              // onChange={handleChange}
              value={""}
              variant="outlined"
              name="so_no"
              size="small"
            />
          </div>
        
          <div className="col-md-3 mb-3">
            <TextField
              label="Vehicle No"
              fullWidth
              // onChange={handleChange}
              // value={cperson_name}
              name="cperson_name"
              variant="outlined"
              size="small"
            />
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="Contact Person"
              fullWidth
              variant="outlined"
              // value={ref_no}
              // name={"contact person"}
              size="small"
              // onChange={handleChange}
              select
            >
              <MenuItem value="ab">ab</MenuItem>
              <MenuItem value="cd">cd</MenuItem>
            </TextField>
          </div>
        
          <div className="col-md-5 mb-3">
            <TextField
              label="Remarks"
              // value={type}
              fullWidth
              variant="outlined"
              size="small"
              // select
              name="type"
              // onChange={handleChange}
            >
              {/* {" "} */}
              {/* <MenuItem value="Project">Project</MenuItem>
              <MenuItem value="Spare">Spare</MenuItem> */}
            </TextField>
          </div>
          <div className="col-md-6 mb-3">
            <TextField
              label="No Of Box"
              fullWidth
              variant="outlined"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Sales Order"
              fullWidth
              variant="outlined"
              value={dis_doc_no}
              onChange={handleChange}
              name="dis_doc_no"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Value"
              fullWidth
              // value={remarks}
              name="remarks"
              variant="outlined"
              // onChange={handleChange}
              size="small"
              multiline
            />
          </div>
        </div> 
        <div className="diList-container">
          {!showDIList && (
            <div className="text-right w-100 mb-5">
              <Button
                color="primary"
                className="mr-2"
                disableElevation
                variant="contained"
                onClick={toggleDIList}
              >
                Pick DI
              </Button>
              <Button
                color="primary"
                className="mr-2"
                disableElevation
                variant="contained"
                disabled={loading}
                onClick={submitMain}
              >
                {loading ? "Processing..." : " Save & Proceed"}
              </Button>
            </div>
          )}
          {showDIList && (
            <TablePicker
              selectedItems={selectedDI}
              columns={diListColumn}
              url={"logistics/dc/pickDI"}
              isNode="node"
              apiBody={{ user_id: getUserId(), sl_type: challanType }}
              apiType="post"
              onPickerClose={toggleDIList}
              type="single"
              onSubmit={handleSubmit}
            />
          )}
        </div>
     
      ///pick dc
      
        {/* <TablePicker
          // selectedItems={selectedPackingList}
          // columns={packagingListColumn}
          url={"dc/pickDC"}
          isNode="node"
          // apiBody={{ company_id: selectedCompany?.company_id }}
          apiType="post"
          // onPickerClose={togglePackagingList}
          // onSubmit={handlePackagingList}
        /> */}
      

{/*     
        <SimpleTable
          // columns={packagingListColumn}
          // rows={selectedPackingList}
          // onDelete={handleAddedCustomer}
        />
       */}

      {

        boxList.map((box, index) => {
          return (
            <div className="box-list mt-5 pt-5" key={index}>
              <div className="d-flex">
                <h2 className="box-header">Box no. {index + 1}</h2>
                <div className=" ml-auto">
                  {/* {showItemList !== index && ( */}
                    <Button
                      color="primary"
                      className="mr-2"
                      disableElevation
                      variant="contained"
                      // onClick={() => toggleItemList(index)}
                    >
                      Pick Item
                    </Button>
                  {/* )} */}

                  <Button
                    color="primary"
                    className="mr-2 ml-auto"
                    disableElevation
                    variant="contained"
                    // onClick={() => removeBox(index)}
                  >
                    Remove Box
                  </Button>
                </div>
              </div>
              <hr />
              
                <TablePicker
                  selectedItems={box.items}
                  // columns={itemListColumn}
                  url={"dc/pickDCItem"}
                  isNode="node"
                  // apiBody={itemListBody}
                  apiType="post"
                  // onPickerClose={() => toggleItemList(null)}
                  // onSubmit={(data) => handleIemList(data, index)}
                />
              
              
                <SimpleTable
                  // columns={itemListColumn}
                  rows={box.items}
                  // onDelete={(item) => deletePickItem(item, index)}
                />
              
            </div>
          );
        })}

      
        <div className="float-action-btn">
          <Fab
            variant="extended"
            // onClick={addBox}
            size="medium"
            color="primary"
            aria-label="add"
          >
            <AddIcon />
            Add Box
          </Fab>
        </div>
      
      <hr />
      <div className="w-100 mt-3 text-right">
        <Button
          color="primary"
          className="mr-2"
          disableElevation
          variant="contained"
          // onClick={submitDC}
        >
          Save
        </Button>
        <Button color="primary" disableElevation variant="contained">
          Cancel
        </Button>
      </div>
    </div>
    
    );
  };
  
  export default AddDeliveryChallanMdc;
