    {box_no:1244,description:"helloddwqd nmait",qty:213,}])
    const [formData, setFormData] = useState({
      di_no: "",
      dc_no: "",
      so_no: "",
      company_id: "",
      company_name: "",
      cperson_id: "",
      di_id: "",
      di_date: "",
      so_date: null,
      cperson_name: "",
      ref_no: "",
      ref_date: null,
      dis_doc_no: "",
      dis_doc_date: null,
      dis_through: "",
      destination: "",
      vehicle_no: "",
      type: "",
      status: "open",
      remarks: "",
      noofbox: "",
      scan_using: "",
      dcDetail: [],
    });
  
