import {
  Button,
  formatMs,
  MenuItem,
  TextField,
  Fab,
} from "@material-ui/core";
import { Autocomplete,} from "@mui/material";
import { DatePicker } from "@material-ui/pickers";

import moment from "moment";
import React, { useEffect, useState } from "react";
import { SimpleTable } from "../../../../components/basic-table";
import {
  debounce,
  
  showErrorToast,
  showSuccessToast,
} from "../../../../components/common";
import { CommonController } from "../../../../_redux/controller/common.controller";

const AddDeliveryChallanMdc = ({ challanType }) => {

  const [tableList, settableList] = useState([]);
  const [formData, setFormData] = useState({
    date: "",
    dc_no: "",
    po_no: "",
    po_date: "",
    party_name: "",
    company_id: "",
    courier: "",
    vehicle_no: "",
    contact_person: "",
    cperson_id: "",
    remarks: "",
    no_of_box: "",
    sales_order: "",
    value: "",
    box_no: "",
    description: "",
    qty: "",
    search: "",
  });
const [contactlist, setcontactlist] = useState(null)
const [saleslist, setsaleslist] = useState(null)
  const [showDIList, setShowDIList] = useState(false);
  const [accountList, setaccountList] = useState([]);
  const [salesOrderList, setsalesOrderList] = useState([])
  const [partychange, setpartychange] = useState({ party_name: "" });
  const toggleDIList = () => setShowDIList(!showDIList);
  const submitAddBox = () => {
          if (formData.box_no==="") {
          showErrorToast("Please Enter Box No");
        }else if (formData.description==="") {
          showErrorToast("Please Enter description");
        }else if (formData.qty==="") {
          showErrorToast("Please Enter Quality");
         } else{
          settableList([
            ...tableList,
            {
              box_no: formData.box_no,
              description: formData.description,
              qty: formData.qty,
            },
          ]);
          setFormData({ ...formData, box_no: "", description: "", qty: "" });
         }
   
  };
  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
    setpartychange({ ...partychange, party_name: event.target.value });
  };
  const handleDateChange = (name, date) => {
    setFormData({ ...formData, [name]: date });
  };

  const handleDeleteTable = (e) => {
    tableList.splice(tableList.indexOf(e), 1);
    settableList([...tableList]);
  };

  const diListColumn = [
    {
      id: "box_no",
      numeric: false,
      disablePadding: false,
      label: "Box No",
    },
    {
      id: "description",
      numeric: false,
      disablePadding: false,
      label: "Description",
    },
    {
      id: "qty",
      numeric: false,
      disablePadding: false,
      label: "Qty",
    },
  ];

  const getDCNo = async () => {
    try {
      await CommonController.commonApiCallFilter(
        "logistics/dc/generateDc",
        {},
        "get",
        "node"
      ).then((data) => {
        if (data.status === 200) {
          setFormData({ ...formData, dc_no: data.data.dc_no });
        }
      });
    } catch (err) {
      showErrorToast(err);
    }
  };
  //Party name
  const getCompanyList = async (search) => {
    // setLoading(true);
    try {
      await CommonController.commonApiCallFilter(
        "logistic/mdc/accountlist",
        { search:search},
        "get",
        "node"
      ).then((data) => {
  console.log(data)
        if (data.status === 200) {
          setaccountList(data.result);
          console.log(accountList)
        }
      });
    } catch (err) {
      showErrorToast(err);
    }
  };
  const getSalesOrder = async () => {
    // setLoading(true);
    try {
      await CommonController.commonApiCallFilter(
        "logistic/mdc/picksomdc",
        "",
        "get",
        "node"
      ).then((data) => {
        console.log(data);
        if (data.status === 200) {
          setsalesOrderList(data.result);
        }
      }).catch(err=>{
        showErrorToast(err.message)
      })
    } catch (err) {
      showErrorToast(err);
    }
  };
  //contact list
  const getAccountContactList = async () => {
    try {
      const search = accountList.filter(
        (val) => contactlist.company_name === val.company_name
      );
    
      const data = search.map((val) => {
        return val.company_id;
      });
      setFormData({ ...formData, company_id: data[0] });
      await CommonController.commonApiCallFilter(
        "logistic/mdc/accountcontactlist",
        { search: data[0] },
        "post",
        "node"
      ).then((data) => {
        // console.log(data.result);
        if (data.status === 200) {
          setFormData({
            ...formData,
            cperson_id: data.result.cperson_id,
            contact_person: data.result.cperson_name,
          });
        }else{
         showErrorToast(data.message)
        }
      }).catch(err=>{
        showErrorToast(err)
      })
    } catch (err) {
      showErrorToast(err);
    }
  };
  const submitMdc = async () => {
    try {
      if (formData.po_no === "") {
        showErrorToast("Please Enter Po No");
      } else if (tableList.length < 0) {
        showErrorToast("Please add box");
      } else if (formData.remarks === "") {
        showErrorToast("Please enter remarks");
      } else if (contactlist.company_name === "") {
        showErrorToast("Please Select Party Name");
      } else if (formData.courier === "") {
        showErrorToast("Please Enter Courier");
      } else if (formData.vehicle_no === "") {
        showErrorToast("Please Enter Vehicle NO");
      } else if (formData.contact_person === "") {
        showErrorToast("Please Enter Contact Person");
      } else if (formData.no_of_box === "") {
        showErrorToast("Please Enter No Of Box");
      } else if (formData.sales_order === "") {
        showErrorToast("Please Enter Sales Order");
      } else if (formData.value === "") {
        showErrorToast("Please Enter Value");
      } else {
        console.log(tableList);
        const body = {
          tran_id: "0",
          date: formData.date,
          dc_no: formData.dc_no,
          so_id: "",
          company_id:contactlist.company_id,
          company_name:contactlist.company_name,
          cperson_id: formData.cperson_id,
          cperson_name: formData.contact_person,
          po_no: formData.po_no,
          po_date: formData.po_date,
          carrier: formData.carrier,
          vehicle: formData.vehicle_no,
          remarks: formData.remarks,
          noofbox: formData.no_of_box,
          value: formData.value,
          user_name: localStorage.getItem("userName"),
          manuDcDetail: tableList,
          user_id: localStorage.getItem("userId"),
        };
        await CommonController.commonApiCallFilter(
          "logistic/mdc/insertmdc",
          body,
          "post",
          "node"
        )
          .then((result) => {
            console.log(result);
            if (result.status === 200) {
              showSuccessToast(result.message);
              // window.location.reload();
              settableList([])
              setFormData({
                ...formData,
                date: "",
                po_no: "",
                po_date: "",
                party_name: "",
                company_id: "",
                courier: "",
                vehicle_no: "",
                contact_person: "",
                cperson_id: "",
                remarks: "",
                no_of_box: "",
                sales_order: "",
                value: "",
                box_no: "",
                description: "",
                qty: "",
                search: "",
              });
            } else {
              showErrorToast(result.message.code);
            }
          })
          .catch((err) => {
            showErrorToast(err);
          });
      }
    } catch (err) {
      showErrorToast(err);
    }
  };
  useEffect(() => {
    getAccountContactList();

  }, [contactlist]);

  useEffect(() => {
    getDCNo();
    debounce(getSalesOrder(),100);
  }, []);

 
 

  const {
    date,
    dc_no,
    po_no,
    po_date,
    courier,
    vehicle_no,
    contact_person,
    remarks,
    no_of_box,
    sales_order,
    value,
    box_no,
    description,
    qty,
    search,
  } = formData;
  return (
    <div className="container-fluid">
      <div className="row mt-5">
        <div className="col-md-3 mb-3">
          <TextField
            label="Date"
            disabled
            fullWidth
            variant="outlined"
            value={moment().format("MM/DD/YYYY")}
            size="small"
            name="date"
          />
        </div>
        <div className="col-md-3 mb-3">
          <TextField
            label="DC NO."
            disabled
            fullWidth
            variant="outlined"
            size="small"
            value={dc_no}
            name={"dc_no"}
          />
        </div>

        <div className="col-md-3 mb-3">
          <TextField
            label="PO No."
            fullWidth
            variant="outlined"
            value={po_no}
            name="po_no"
            size="small"
            onChange={handleChange}
          />
        </div>
        <div className="col-md-3 mb-3">
          <DatePicker
            label="PO Date"
            // value={po_date}
            format="dd/MM/yyyy"
            onChange={(date) => handleDateChange("po_date", date)}
            animateYearScrolling
            inputVariant="outlined"
            size="small"
            autoOk
            fullWidth
          />
        </div>
        <div className="col-md-6 mb-3">
        <Autocomplete
              id="combo-box-demo"
              options={accountList}
              fullWidth
              size="small"
              getOptionLabel={(option) =>option.company_name}
              value={contactlist}
              onChange={(e, value) => setcontactlist(value)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  onInput={(e) => {
                    debounce(getCompanyList(e.target.value, 200));
                  }}
                  label="Party Name"
                  variant="outlined"
                />
              )}
            />
          {/* <TextField
            label="Party Name"
            fullWidth
            variant="outlined"
            size="small"
            value={party_name}
            name="party_name"
            // disabled
            onChange={handleChange}
            select
          >
            {accountList.map((val) => (
              <MenuItem key={val.company_id} value={val.company_name}>
                {val.company_name}
              </MenuItem>
            ))}
          </TextField> */}
        </div>
        <div className="col-md-3 mb-3">
          <TextField
            label="Courier"
            fullWidth
            onChange={handleChange}
            value={courier}
            variant="outlined"
            name="courier"
            size="small"
          />
        </div>

        <div className="col-md-3 mb-3">
          <TextField
            label="Vehicle No"
            fullWidth
            value={vehicle_no}
            onChange={handleChange}
            name={"vehicle_no"}
            variant="outlined"
            size="small"
          />
        </div>
        <div className="col-md-6 mb-3">
          <TextField
            label="Contact Person"
            fullWidth
            variant="outlined"
            value={contact_person}
            name={"contact_person"}
            size="small"
            onChange={handleChange}
            // select
          >
            {/* <MenuItem value="ab">ab</MenuItem> */}
          </TextField>
        </div>

        <div className="col-md-6 mb-3">
          <TextField
            label="Remarks"
            value={remarks}
            fullWidth
            variant="outlined"
            size="small"
            name="remarks"
            onChange={handleChange}
          />
        </div>
        <div className="col-md-6 mb-3">
          <TextField
            label="No Of Box"
            value={no_of_box}
            name="no_of_box"
            fullWidth
            variant="outlined"
            size="small"
            onChange={handleChange}
          />
        </div>
        <div className="col-md-3 mb-3">
        <Autocomplete
              id="combo-box-demo"
              options={salesOrderList}
              fullWidth
              size="small"
              getOptionLabel={(option) =>option.so_no}
              value={saleslist}
              onChange={(e, value) => setsaleslist(value)}
              sx={{ height:10 }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  onClick={(e) => {
                    debounce(getSalesOrder( 200));
                  }}
                  label="Sales Order"
                  size="small"
                  variant="outlined"
                />
              )}
            />
          {/* <TextField
            label="Sales Order"
            fullWidth
            variant="outlined"
            value={sales_order}
            onChange={handleChange}
            name="sales_order"
            size="small"
            select
          >
           {salesOrderList.map((val,i)=>( <MenuItem key={i }value={val.so_no}> {val.so_no}</MenuItem>))}
          </TextField> */}
        </div>
        <div className="col-md-3 mb-3">
          <TextField
            label="Value"
            fullWidth
            value={value}
            name="value"
            variant="outlined"
            onChange={handleChange}
            size="small"
            multiline
          />
        </div>
      </div>

      {tableList.length > 0 ? (
        <SimpleTable
          columns={diListColumn}
          rows={tableList}
          onDelete={handleDeleteTable}
        />
      ) : null}
      <hr />
      <div className="container-fluid">
        {/* <h1>Add Box:</h1> */}
        <div className="row mt-5">
          <div className="col-md-3 mb-3">
            <TextField
              label="Box No"
              fullWidth
              onChange={handleChange}
              value={box_no}
              variant="outlined"
              name="box_no"
              size="small"
            />
          </div>
          <div className="col-md-4 mb-3">
            <TextField
              label="Description"
              fullWidth
              onChange={handleChange}
              value={description}
              variant="outlined"
              name="description"
              size="small"
            />
          </div>
          <div className="col-md-3 mb-3">
            <TextField
              label="Qty"
              fullWidth
              onChange={handleChange}
              value={qty}
              variant="outlined"
              name="qty"
              type="number"
              size="small"
            />
          </div>
          <div className="col-md-2 mb-3 pl-5">
            <Button
              color="primary"
              // className="mr-2"
              // disableElevation
              variant="contained"
              onClick={submitAddBox}
            >
              Add New
            </Button>
          </div>
        </div>
      </div>

      {/* <div className="float-action-btn">
          <Fab
            variant="extended"
            onClick={addBox}
            size="medium"
            color="primary"
            aria-label="add"
          >
            <AddIcon />
            Add Box
          </Fab>
        </div> */}

      <hr />
      <div className="w-100 mt-3 text-right">
        <Button
          color="primary"
          className="mr-2"
          disableElevation
          variant="contained"
          onClick={submitMdc}
        >
          Save
        </Button>
        <Button color="primary" disableElevation variant="contained">
          Cancel
        </Button>
      </div>
    </div>
  );
};

export default AddDeliveryChallanMdc;
